import React, { Suspense, useEffect, useState } from "react";
import { Routes, Route } from 'react-router-dom';
import Footer from "./components/layouts/Footer";
import Header from "./components/layouts/Header";
import Main from './components/pages/Main';
import Elado from './components/pages/Elado';
import References from './components/pages/References';
import MostEpuls from './components/pages/MostEpuls';
import ReferenceDetail from './components/ReferenceDetail';
import MostEpulDetail from './components/MostEpulDetail';
import UrlMaker from "./components/UrlMaker";
const ReferenceContext  = React.lazy(() => import('./components/ReferenceContext'));
const MostEpulProvider  = React.lazy(() => import('./components/MostEpulContext'));

const App = props => {
  // State for references
  const [references, setReferences] = useState([])

  // State for letting know when is the content fetched
  const [isLoaded, setIsLoaded] = useState(false)

  // Fetch data from CMS
  useEffect(() => {
      const url = `https://api-eu-central-1.graphcms.com/v2/ckwq5z05y2e8n01xmgomm92ka/master?query=query%20MyQuery%20%7B%0A%20%20referenciaks(orderBy%3A%20updatedAt_DESC)%20%7B%0A%20%20%20%20id%0A%20%20%20%20title%0A%20%20%20%20gallery%20%7B%0A%20%20%20%20%20%20fileName%0A%20%20%20%20%20%20url%0A%20%20%20%20%20%20id%0A%20%20%20%20%7D%0A%20%20%7D%0A%7D&operationName=MyQuery`       
      fetch(url)
      .then(res => res.json())
      .then(json => {
          setReferences(json.data.referenciaks);
          setIsLoaded(true);
          });
  },[])

  if (isLoaded) {
      references.forEach(reference => {
          reference.url = UrlMaker(reference.title)
      })
  }
  return (
    <div className="App">
      <Header />
      <Suspense fallback={<div>Betoltes...</div>}>
      <ReferenceContext.Provider value={references}>
      <MostEpulProvider>
        <Routes>
            <Route exact path='/' element={<Main />}/>
            <Route path='/referenciak' element={<References />}/>
            <Route path='/referenciak/:project' element={<ReferenceDetail />}/>
            <Route path='/most-epul' element={<MostEpuls />}/>
            <Route path='/most-epul/:project' element={<MostEpulDetail />}/>
            <Route path='/elado' element={<Elado />}/>
        </Routes>
      </MostEpulProvider>
      </ReferenceContext.Provider>
      </Suspense>
      <Footer />
    </div>
  );
}

export default App;
