import React, { Suspense, useEffect, useState } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import SwiperCore, { Navigation, Pagination, Autoplay } from 'swiper';

import 'swiper/swiper-bundle.css'


SwiperCore.use([Navigation, Pagination, Autoplay]);

// Import Swiper styles


const MySlider = props => {
    const [slider, setSlider] = useState([]);

    useEffect(() => {
        const url = `https://api-eu-central-1.graphcms.com/v2/ckwq5z05y2e8n01xmgomm92ka/master?query=query%20MyQuery%20%7B%0A%20%20sliders%20%7B%0A%20%20%20%20pictures%20%7B%0A%20%20%20%20%20%20id%0A%20%20%20%20%09url%0A%20%20%20%20%7D%0A%20%20%7D%0A%7D&operationName=MyQuery`;
        fetch(url)
        .then(res => res.json())
        .then(json => setSlider(json.data.sliders[0].pictures));
    }, []);

    return (
        <Swiper
            slidesPerView={1.5}
            spaceBetween={30}
            centeredSlides={true}
            loop={true}
            autoplay={{
                delay:3000,
                disableOnInteraction: false
            }}
            pagination={{ clickable: true }} 
            navigation={true}>
            {slider.map(slide => {
                const url = React.lazy(() => import(slide.url))
                console.log(url._payload)
                return(
                <Suspense fallback={<div>Betoltes...</div>} key={slide.id}>
                    <SwiperSlide className='swiper-slide' ><div style={{backgroundImage:"url(" + url + ")"}}></div></SwiperSlide>
                </Suspense>
            )})}
        </Swiper>
    )
}

export default MySlider;
